package xrpl

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestMultisign(t *testing.T) {
	testCases := []struct {
		name string
		blobs []string
		want string
		err error
	}{
		{
			name: "pass - valid blobs",
			blobs: []string{"", ""},
			want: "",
		},
		{
			name: "fail - no blobs",
			blobs: []string{},
			err: ErrNoTxToMultisign,
		},
	}

	for _, tc := range testCases {
		res, err := Multisign(tc.blobs...)
		if tc.err != nil {
			require.Error(t, err)
		} else {
			require.Equal(t, tc.want, res)
		}
	}
}
